// src/app/settings/settings.page.ts

import { Component } from '@angular/core';
import { AuthenticationService } from '../services/authentication.service';

@Component({
  selector: 'app-settings',
  templateUrl: './settings.page.html',
  styleUrls: ['./settings.page.scss'],
})
export class SettingsPage {
  user: any = {}; // Replace with your User type if defined
  newProfilePicture: File | null = null;

  constructor(private authService: AuthenticationService) {}

  ngOnInit() {
    this.user = this.authService.getCurrentUser();
  }

  onFileChange(event: any) {
    const file = event.target.files[0];
    if (file) {
      this.newProfilePicture = file;
      this.user.profilePicture = file; // Set the file in the user object
    }
  }

  updateUserInfo() {
    const updatedUserData = {
      id: this.user.id,
      name: this.user.name,
      username: this.user.username,
      email: this.user.email,
      profilePicture: this.newProfilePicture, // Include the profile picture
    };

    // Ensure that updateUser is called with correct number of arguments
    this.authService.updateUser(updatedUserData).subscribe(
      (response: any) => {
        console.log('User information updated successfully:', response);
        alert('User updated successfully');
      },
      (error: any) => {
        console.error('Error updating user information:', error);
        alert('Error updating user');
      }
    );
  }
}
<ion-header>
  <ion-toolbar>
    <ion-buttons slot="start">
      <ion-back-button defaultHref="/home"></ion-back-button>
    </ion-buttons>
    <ion-title>Ajustes</ion-title>
    <ion-buttons slot="end">
      <ion-button (click)="updateUserInfo()">Guardar</ion-button>
    </ion-buttons>
  </ion-toolbar>
</ion-header>

<ion-content class="settings-content">
  <!-- Profile Section -->
  <div class="profile-section">
    <img 
      [src]="newProfilePicture ? newProfilePicture : (user?.profilePicture ? '/uploads/' + user?.profilePicture : '/assets/images/9187604.png')" 
      alt="Profile Picture" 
      class="profile-picture"
    />
    <ion-button color="medium" expand="block" (click)="fileInput.click()">
      Cambiar foto de perfil
    </ion-button>
    <input type="file" accept="image/*" #fileInput (change)="onFileChange($event)" hidden />
  </div>

  <!-- Form to Update User Information -->
  <ion-card>
    <ion-card-header>
      <ion-card-title>Actualizar datos</ion-card-title>
    </ion-card-header>
    <ion-card-content>
      <form (ngSubmit)="updateUserInfo()">
        <!-- Name Input -->
        <ion-item>
          <ion-label position="floating">Nombre</ion-label>
          <ion-input [(ngModel)]="user.nombre" name="nombre" required></ion-input>
        </ion-item>

        <!-- Username Input -->
        <ion-item>
          <ion-label position="floating">Nombre de Usuario</ion-label>
          <ion-input [(ngModel)]="user.nombreUsuario" name="nombreUsuario" required></ion-input>
        </ion-item>

        <!-- Email Input -->
        <ion-item>
          <ion-label position="floating">Email</ion-label>
          <ion-input [(ngModel)]="user.correo" name="correo" type="email" required></ion-input>
        </ion-item>

        <!-- Password Input -->
        <ion-item>
          <ion-label position="floating">Contraseña</ion-label>
          <ion-input [(ngModel)]="user.contrasena" name="contrasena" type="password"></ion-input>
        </ion-item>

        <!-- Date of Birth Input -->
        <ion-item>
          <ion-label position="floating">Fecha de Nacimiento</ion-label>
          <ion-datetime
            display-format="DD/MM/YYYY"
            picker-format="DD/MM/YYYY"
            [(ngModel)]="user.fechaNacimiento"
            name="fechaNacimiento">
          </ion-datetime>
        </ion-item>

        <!-- Update Button -->
        <ion-button expand="full" color="primary" type="submit">Actualizar</ion-button>
      </form>
    </ion-card-content>
  </ion-card>
</ion-content>
