// src/app/recovery/recovery.page.ts
import { Component } from '@angular/core';
import { NavController } from '@ionic/angular';
import { getAuth, sendPasswordResetEmail } from 'firebase/auth';

@Component({
  selector: 'app-recovery',
  templateUrl: './recovery.page.html',
  styleUrls: ['./recovery.page.scss'],
})
export class RecoveryPage {
  email: string = '';

  constructor(private navCtrl: NavController) {}

  async sendRecoveryEmail() {
    if (this.email) {
      try {
        const auth = getAuth();
        await sendPasswordResetEmail(auth, this.email);
        alert('Recovery email sent! Please check your inbox.');
      } catch (error) {
        alert('No account found with that email address.');
        console.error('Error sending recovery email:', error);
      }
    } else {
      alert('Please enter your email address.');
    }
  }

  navigateBackToLogin() {
    this.navCtrl.navigateBack('/login');
  }
}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>Password Recovery</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <div class="card-container">
    <ion-card>
      <ion-card-header>
        <ion-card-title>Recover Your Password</ion-card-title>
      </ion-card-header>

      <ion-card-content>
        <ion-item>
          <ion-label position="floating">Email</ion-label>
          <ion-input type="email" [(ngModel)]="email" required></ion-input>
        </ion-item>

        <ion-button (click)="sendRecoveryEmail()" shape="round" color="secondary" expand="full">Send Recovery Email</ion-button>

        <div class="back-to-login">
          <ion-label color="medium" (click)="navigateBackToLogin()" style="cursor: pointer;">
            Back to Login
          </ion-label>
        </div>
      </ion-card-content>
    </ion-card>
  </div>
</ion-content>
