// src/app/register/register.page.ts
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { NgForm } from '@angular/forms';
import { getAuth, createUserWithEmailAndPassword, updateProfile } from 'firebase/auth';

@Component({
  selector: 'app-register',
  templateUrl: './register.page.html',
  styleUrls: ['./register.page.scss'],
})
export class RegisterPage {
  name: string = '';
  username: string = '';
  email: string = '';
  password: string = '';
  confirmPassword: string = '';

  constructor(private router: Router) {}

  register(form: NgForm) {
    if (this.password !== this.confirmPassword) {
      alert('Passwords do not match');
      return;
    }

    const auth = getAuth();
    createUserWithEmailAndPassword(auth, this.email, this.password)
      .then((userCredential) => {
        // Successfully registered
        updateProfile(userCredential.user, { displayName: this.username });
        alert('Registration successful! You can now log in.');
        this.router.navigate(['/login']);
      })
      .catch((error) => {
        console.error('Registration error:', error);
        alert('An error occurred during registration.');
      });
  }

  navigateToLogin() {
    this.router.navigate(['/login']);
  }
}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-buttons slot="start">
      <ion-button (click)="navigateToLogin()">
        <ion-icon slot="icon-only" name="arrow-back-outline"></ion-icon>
      </ion-button>
    </ion-buttons>
    <ion-title>Registrarse</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true" class="register-content">
  <div class="background-pattern">
    <img src="assets/images/fondo.png" alt="Background Pattern" class="background-fill" />
  </div>

  <!-- Registration Card Section -->
  <div class="register-card">
    <h1>Registrarse</h1>
    <p>¿Ya estás registrado? <a (click)="navigateToLogin()">Entra aquí</a></p>

    <form (ngSubmit)="register(registerForm)" #registerForm="ngForm">
      <!-- Name Input -->
      <ion-item lines="none" class="input-item">
        <ion-label position="stacked">NOMBRE</ion-label>
        <ion-input type="text" [(ngModel)]="name" name="name" placeholder="Nombre" required></ion-input>
      </ion-item>

      <!-- Username Input -->
      <ion-item lines="none" class="input-item">
        <ion-label position="stacked">NOMBRE DE USUARIO</ion-label>
        <ion-input type="text" [(ngModel)]="username" name="username" placeholder="Nombre de Usuario" required></ion-input>
      </ion-item>

      <!-- Email Input -->
      <ion-item lines="none" class="input-item">
        <ion-label position="stacked">EMAIL</ion-label>
        <ion-input type="email" [(ngModel)]="email" name="email" placeholder="Correo electrónico" required></ion-input>
      </ion-item>

      <!-- Password Input -->
      <ion-item lines="none" class="input-item">
        <ion-label position="stacked">CONTRASEÑA</ion-label>
        <ion-input type="password" [(ngModel)]="password" name="password" placeholder="••••••" required></ion-input>
      </ion-item>

      <!-- Confirm Password Input -->
      <ion-item lines="none" class="input-item">
        <ion-label position="stacked">CONFIRMAR CONTRASEÑA</ion-label>
        <ion-input type="password" [(ngModel)]="confirmPassword" name="confirmPassword" placeholder="••••••" required></ion-input>
      </ion-item>

      <!-- Submit Button -->
      <ion-button expand="block" type="submit" [disabled]="!registerForm.valid" class="register-button">Registrarse</ion-button>
    </form>
  </div>
</ion-content>
